/**
 * Core librería
 */
angular.module("proyecto", [
    'ui.router',
    'ngMaterial'
]);
/**
 * Created by guzman on 4/02/17.
 */



(function() {
    function routes($stateProvider,$locationProvider){
        $locationProvider.html5Mode(true);
        $stateProvider
            .state('login',{
                url: "/inicio",
                controller: 'loginCtrl',
                controllerAs: 'lc',
                templateUrl: '/login'
            })

            .state('registro',{
                url: "/signup",
                controller: 'registroCtrl',
                controllerAs: 'rc',
                templateUrl: '/registro'
            })
    }

    angular.module('proyecto')
        .config(['$stateProvider', '$locationProvider', routes]);

})();


/**
 * Created by guzman on 4/02/17.
 */


(function() {
    function loginCtrl($http){
        var vm = this;
        vm.login = function(){

            console.log("Info login", vm.usernameModel, vm.passwordModel)
            /**
             * TODO: Llamada al servidor
             */
            var json = {
                usernameServer : vm.usernameModel,
                passwordServer : vm.passwordModel
            };

            $http.post('/loginForm',json)
                .then(function(response){
                        if (response.data.codigo === 500)
                            swal(
                                'Oops...',
                                'Error al iniciar sesión',
                                'error'
                            )
                        else
                            swal(
                                ' ',
                                'Has iniciado sesion',
                                'success'
                            )
                        console.log("respuesta",response);
                        vm.status = "OK";

                    },
                    function(response) {
                        swal(
                            'Oops...',
                            'Error de servidor',
                            'error'
                        )

                    });
        }
    }

    angular.module('proyecto')
        .controller('loginCtrl',['$http',loginCtrl]);

})();

/**
 * Created by guzman on 4/02/17.
 */


(function() {
    function registroCtrl($http){
        var vm = this;
        vm.registro = function(){
            var json = {
                usernameRegistro : vm.usernameModel,
                passwordRegistro : vm.passwordModel,
                emailRegistro : vm.emailModel
            };

            $http.post('/registerForm',json)
                .then(function(response){
                        console.log("respuesta",response);
                        vm.status = "OK";
                    },
                    function(response) {
                        vm.status = "Fallo";
                        console.log("fallo")
                        swal(
                            'Oops...',
                            'Error al registrar!',
                            'error'
                        )
                    });

        }
    }

    angular.module('proyecto')
        .controller('registroCtrl',['$http',registroCtrl]);

})();
